const investmentsLinks=document.querySelectorAll(".investments-nav-link"),drawer=document.querySelector(".drawer");for(let e=0;e<investmentsLinks.length;++e)investmentsLinks[e].addEventListener("click",e=>{e.preventDefault(),closeDrawer().then(()=>switchPg(document.querySelector(".home-page"),document.querySelector(".investments-page"),()=>{document.querySelector(".navbar-brand").classList.add("show-logo"),document.querySelector(".page-wrapper").style.overflowY="auto"}))});document.querySelector(".nav-logo").addEventListener("click",e=>{e.preventDefault(),switchPg(document.querySelector(".investments-page"),document.querySelector(".home-page"),()=>{document.querySelector(".navbar-brand").classList.remove("show-logo");const e=document.querySelector(".page-wrapper");e.scrollTop=0,e.style.overflowY="hidden"})});const onAnimationComplete=e=>{drawer.removeEventListener("hidden.bs.modal",onAnimationComplete),e()},closeDrawer=()=>new Promise((e,t)=>{drawer.classList.contains("show")?(drawer.addEventListener("hidden.bs.modal",t=>onAnimationComplete(e),!1),document.querySelector(".close").click()):e()}),switchPg=(e,t,n)=>{e.classList.remove("page-active"),e.classList.add("page-inactive"),t.classList.remove("page-inactive"),t.classList.add("page-active"),n()};