document.querySelector("#investments-nav-link").addEventListener("click",e=>{e.preventDefault(),switchPg(document.querySelector(".home-page"),document.querySelector(".investments-page"),()=>document.querySelector(".navbar-brand").classList.add("show-logo"))}),document.querySelector(".nav-logo").addEventListener("click",e=>{e.preventDefault(),switchPg(document.querySelector(".investments-page"),document.querySelector(".home-page"),()=>document.querySelector(".navbar-brand").classList.remove("show-logo"))});let switchPg=(e,t,a)=>{e.classList.remove("page-active"),e.classList.add("page-inactive"),t.classList.remove("page-inactive"),t.classList.add("page-active"),a()};